const jimp = require(`jimp`);

async function trash(image) {
    if (!image) throw new Error(`Please provide a image url.`)

    let bg = await jimp.read(`https://raw.githubusercontent.com/caseyCantCode/discordImageGenImages/main/trash.png`);
    image = await jimp.read(image);
    image.resize(309, 309);
    image.blur(5);
    bg.composite(image, 309, 0);
    let raw;
    bg.getBuffer(`image/png`, (err, buffer) => {
        raw = buffer;
    });
    return raw;
}
module.exports = trash